import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info("hi")
//*Context is refer only in current TestStep file
log.info context.expand('${#TestCase#id}')
// or def expectedValue = context.getTestCase().getPropertyValue("id")
//Using ***testRunner** we can navigae to any folder/files
log.info testRunner.testCase.getPropertyValue("id")
//TestStep flow: Current TestStep--> TestCase--> TestSuite--> TestCases[]--> testStep
//get value of addEmplyee TestCase
log.info testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("dept")
//Set value of addEmplyee TestCase
testRunner.testCase.testSuite.testCases["addEmployee TestCase"].setPropertyValue("dept", "IT")
//Verify value of addEmplyee TestCase is update
log.info testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("dept")
//Get Request data of AddEmplyee TestStep
log.info testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"].getPropertyValue("Request")


//********Atuomation Start from Here****************
//Automation to Set the AddEmplyee value to request form Custom Properties

//1st Load the addEmplyee Request XML
def addReq=testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"].getPropertyValue("Request")
//This XmlHolder method will load the xml of Add Employee Request
//Add package*******"import com.eviware.soapui.support.XmlHolder"********
def xmlAdd= new XmlHolder(addReq)

//Get the Custom Properties data from AddEmployee TestCase
def id=testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("id")
def name=testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("name")
def dept=testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("dept")
def age=testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue("age")

//Assign the custom properties value to XML Request of AddEmployee testStep
xmlAdd.setNodeValue("//typ:addEmployee/typ:name", name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id", id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department", dept)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age", age)
//Above code will only set the data, it doent intilize it, So below line will intilize
//Now set the value to the XML request
def newAddXml= xmlAdd.getXml();
testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"].setPropertyValue("Request",newAddXml)
//To Run AddEmployee request
def addTestStep = testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"]
//Add package*******com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext******to map Tests
def contextAddEmplyee = new WsdlTestRunContext(addTestStep);
addTestStep.run(testRunner,contextAddEmplyee)

import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
//This will get the Request Object and Hold it in XMLHolder
def reqData= testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"].getPropertyValue('Request')
def xmlData= new XmlHolder(reqData)

def name= testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue('name')
def id= testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue('id')
def age= testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue('age')
def dept= testRunner.testCase.testSuite.testCases["addEmployee TestCase"].getPropertyValue('dept')

log.info dept 
//This set the SetNodeVale of name, id...
xmlData.setNodeValue("//typ:addEmployee/typ:name", name)
xmlData.setNodeValue("//typ:addEmployee/typ:id", id)
xmlData.setNodeValue("//typ:addEmployee/typ:age", age)
xmlData.setNodeValue("//typ:addEmployee/typ:Department", dept)

//This will SAVE the SetNodeValue to assignToRequest variable
def assignToRequest = xmlData.getXml();
//This will assigng the value of AssignToRequest to Request using Request XML
testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"].setPropertyValue("Request",assignToRequest)

def addtoRun= testRunner.testCase.testSuite.testCases["addEmployee TestCase"].testSteps["addEmployee"]
def wsdlContext = new WsdlTestRunContext(addtoRun)
addtoRun.run(testRunner,wsdlContext)

